import React, {useState, useEffect} from "react"

export const EmprestimoContext = React.createContext()

const EmprestimoContextProvider = () => {

    const [hasErrors,setErrors] = useState(false)
    const [emprestimo,setEmprestimo] = useState({})

  async function fetchData() {
    const res = await fetch("http://www.mocky.io/v2/5c923b0932000029056bce39/totalAmountInTaxes")
    res
      .json()
      .then(res => setEmprestimo(res))
      .catch(err => setErrors(err))
  }
  useEffect(() => {
    fetchData()
  },[true])
        return(
            <EmprestimoContext.Provider value={{...this.state}}>
            console.log({emprestimo})
            </EmprestimoContext.Provider>
        )
}

export default EmprestimoContext
