import React, { useState, useEffect } from "react"
import "./App.css"
import Dashboard from "./Components/Dashboard"


const App = props => {

  const [hasErrors,setErrors] = useState(false)
  const [emprestimo,setEmprestimo] = useState({})

  async function fetchData() {
    const res = await fetch("http://www.mocky.io/v2/5c923b0932000029056bce39/totalAmountInTaxes")
    res
      .json()
      .then(res => setEmprestimo(res))
      .catch(err => setErrors(err))
  }

  useEffect(() => {
    fetchData()
  })
  const EmprestimoContext = React.createContext(emprestimo)



  return (
    <EmprestimoContext value={"http://www.mocky.io/v2/5c923b0932000029056bce39/totalAmountInTaxes"}>
      <Dashboard />
      </EmprestimoContext> 
    )
}

export default App
